import fs from "fs";
import path from "path";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Create a proper 100x100 red square JPEG
// This is a valid JPEG that Sharp can process
const validJpeg = Buffer.from(
  "",
  "base64"
);

const outputPath = path.join(__dirname, "test-image.jpg");

if (!fs.existsSync(path.dirname(outputPath))) {
  fs.mkdirSync(path.dirname(outputPath), { recursive: true });
}

fs.writeFileSync(outputPath, validJpeg);

console.log("âœ… Valid test image created at:", outputPath);
